import { useState, useEffect } from 'react';

import { Navigation } from '../index.js';

import '../pages_css/projects.css'
import '../assets/css/components.css'
import { useFetch } from '../assets/js/communication.js';

function ResearchBar() {
	return (
		<div className='research_container'>
			<input type="text" className='research' placeholder="HTML, C#, FLUTTER, ...">
			</input>
			<button onClick={onResearch()} className="button_project">
				Search
			</button>
		</div>
	)
}

export function Projects(props) {

	[window.projectLoading, window.projectError, window.projectData] = useFetch("api/projects?populate=*&sort=Order%3Adesc");

	if (window.projectError) {
		return <p>Error.</p>;
	}

	if (window.projectLoading) {
		return <p>Loading...</p>;
	}

	let project = window.projectData.data;

	return (<>
		<Navigation highlight='projects' />
		<ResearchBar />
		<div className='card_holder'>
			{
				project.map((data) => {
					return (<div className='card card_projects' key={data.id}>
						<div className='card_element card_element_projects card_element_tags'>

							{data.attributes.Tags.replaceAll(',',"<br/>")}

						</div>
						<div className='card_element card_element_projects card_element_content'>

						</div>
						<div className='card_element card_element_projects card_element_image'>

						</div>
					</div>);
				})
			}


		</div>
	</>)
}

function onResearch() {

}
